refentry.refsect1:
  &reftitle.parameters;




     value



       Valor para filtrar. Observe que valores escalares são convertidos para
       strings internamente antes de serem filtrados.



    &filter.param.filter;

     options



       Array associativo de opções ou disjunção binária de opções. Se o filtro
       aceitar opções, elas podem ser fornecidas no campo "flags" do array. Para
       um filtro de função de retorno, um tipo callable deve ser passado. Esta
       função precisa aceitar um argumento, o valor a ser filtrado, e retornar
       o valor depois de filtrá-lo ou sanitizá-lo.





<?php
// Para filtros que aceitam opções, use este formato
$options = array(
    'options' => array(
        'default' => 3, // valor a retornar se o filtro falhar
        // outras opções aqui
        'min_range' => 0
    ),
    'flags' => FILTER_FLAG_ALLOW_OCTAL,
);
$var = filter_var('0755', FILTER_VALIDATE_INT, $options);

// Para filtros que aceitam somente opções, pode-se passá-las diretamente
$var = filter_var('oops', FILTER_VALIDATE_BOOLEAN, FILTER_NULL_ON_FAILURE);

// Para filtros que aceitam somente opções, pode-se também passá-las como um array
$var = filter_var('oops', FILTER_VALIDATE_BOOLEAN,
                  array('flags' => FILTER_NULL_ON_FAILURE));

// função de retorno de validação de filtro
function foo($value)
{
    // Formato esperado: Sobrenome ($surname), Nomes ($givennames)
    if (strpos($value, ", ") === false) return false;
    list($surname, $givennames) = explode(", ", $value, 2);
    $empty = (empty($surname) || empty($givennames));
    $notstrings = (!is_string($surname) || !is_string($givennames));
    if ($empty || $notstrings) {
        return false;
    } else {
        return $value;
    }
}
$var = filter_var('Doe, Jane Sue', FILTER_CALLBACK, array('options' => 'foo'));
?>

  &reftitle.examples;



    Um exemplo de filter_var


<?php
var_dump(filter_var('bob@example.com', FILTER_VALIDATE_EMAIL));
var_dump(filter_var('example.com', FILTER_VALIDATE_URL, FILTER_FLAG_SCHEME_REQUIRED));
?>


    &example.outputs;


string(15) "bob@example.com"
bool(false)







    Exemplo de um filtro de um array


<?php
$emails = [
    "bob@example.com",
    "test@example.local",
    "invalidemail"
];

var_dump(filter_var($emails, FILTER_VALIDATE_EMAIL, FILTER_REQUIRE_ARRAY));
?>


    &example.outputs;


array(3) {
  [0]=>
  string(15) "bob@example.com"
  [1]=>
  string(18) "test@example.local"
  [2]=>
  bool(false)
}
